mapper.update:
update course
set name = #{name,jdbcType=VARCHAR},
    dept = #{dept,jdbcType=VARCHAR},
    address = #{address,jdbcType=VARCHAR},
    credit = #{credit,jdbcType=DECIMAL}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, dept, address, credit
from course
where id = #{id,jdbcType=VARCHAR}
