mapper.update:
update course
set name = #{name,jdbcType=VARCHAR},
    teacher_id = #{teacherId,jdbcType=VARCHAR},
    address = #{address,jdbcType=VARCHAR},
    credit = #{credit,jdbcType=DECIMAL}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, teacher_id, address, credit
from course
where id = #{id,jdbcType=VARCHAR}

select c.id as id, c.name as name, c.teacher_id as teacher_id, c.address as address, c.credit as credit, t.name as teacherName from course as c, teacher as t where c.teacher_id = t.id
and c.name like CONCAT('%',#{name},'%')
and t.name = #{teacherName}
